mapper.update:
UPDATE order_item
SET pid = #{pid}, uid = #{uid}, oid = #{oid}, number = #{number}, has_review = #{has_review}
WHERE id = #{id}

mapper.select:
SELECT sum(number)
FROM order_item
WHERE pid = #{pid}

SELECT *
FROM order_item
WHERE oid = -1 AND uid = #{uid} AND pid = #{pid}

SELECT *
FROM order_item
WHERE oid = -1 AND uid = #{uid}

SELECT SUM(number)
FROM order_item
WHERE pid = #{pid}
