mapper.update:
UPDATE order_
SET status = 'DELETED'
WHERE id = #{id}

UPDATE order_
SET pay_date = #{payDate}, delivery_date = #{deliveryDate}, confirm_date = #{confirmDate},
  status     = #{orderStatus,typeHandler=OrderStatusEnumHandler}
WHERE id = #{id}

mapper.select:

WHERE uid = #{uid} AND status != 'DELETED'
ORDER BY id DESC
LIMIT #{count} OFFSET #{start}
